import React, { useState } from "react";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import DialogContent from "@material-ui/core/DialogContent";
import DialogActions from "@material-ui/core/DialogActions";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import MenuItem from "@material-ui/core/MenuItem";

export interface RegularField {
  label : string
  type : "text" | "number" | "date"
}

export interface SelectionField {
  label : string
  type : "selection"
  items : string[]
}

export type Field = RegularField | SelectionField

export interface InputDialogProps<T extends {[key: string]: any }> {
  open : boolean
  title : string
  defaultValue : T
  fields : Record<keyof T, Field>
  onClose : (state : T | null) => Promise<void>
}

export function AlertDialog<T extends { [key : string] : any }>(props : InputDialogProps<T>) {
  const [ state, setState ] = useState<T>(props.defaultValue);

  function fieldsToInput([fieldName, field] : [string, Field], index : number) : JSX.Element {
    if (field.type === "selection") {
      return (
        <FormControl key={index} fullWidth>
          <InputLabel required>{field.label}</InputLabel>
          <Select
              value={state[fieldName]}
              defaultValue={""}
              onChange={e => setState({ ...state, [fieldName]: e.target.value })}>
            {field.items.map(item => (<MenuItem key={item} value={item}>{item}</MenuItem>))}
          </Select>
        </FormControl>
      )
    } else {
      return (
        <TextField
          required
          autoFocus
          fullWidth
          key={index}
          label={field.label}
          type={field.type}
          onChange={e => setState({ ...state, [fieldName]: e.target.value })}
          InputLabelProps={{
            shrink:true,
            required:true,
          }}
          placeholder={(field.type==="date")?"YYYY-MM-DD":""}
        />
      )
    }
  }
  const fieldsAsArray : [string, Field][] = Object.entries(props.fields);

  return (
    <div>
      {/* <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open alert dialog
      </Button> */}
      <Dialog
        open={props.open} onClose={() => props.onClose(null)}
        // onClose={() => props.onClose(null)}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
          {/* <DialogContentText id="alert-dialog-description"> */}
            Let Google help apps determine location. This means sending anonymous location data to
            Google, even when no apps are running.
          {/* </DialogContentText> */}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => props.onClose(null)} color="primary">
            Disagree
          </Button>
          <Button onClick={() => props.onClose(state)} color="primary" autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
